import React, { useState } from "react";
import axios from "axios";

const DocToPdfConverter = () => {
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setError(null);
      setSuccess(false);
    }
  };

  const handleConvert = async () => {
    if (!file) {
      setError("Please select a file first.");
      return;
    }

    setLoading(true);
    setError(null);
    setSuccess(false);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:3001/convert", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      // Handle the response, e.g., download the PDF file
      const fileBlob = new Blob([response.data], { type: "application/pdf" });
      const downloadUrl = URL.createObjectURL(fileBlob);
      const link = document.createElement("a");
      link.href = downloadUrl;
      link.download = "converted-file.pdf";
      link.click();

      setSuccess(true);  // Mark as successful
    } catch (error) {
      console.error("Error during conversion:", error);
      setError("Conversion failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-semibold mb-4 text-center">Convert DOC to PDF</h2>
      <div className="mb-4">
        <label htmlFor="file" className="block text-lg font-medium text-gray-700">
          Upload DOCX file
        </label>
        <input
          type="file"
          id="file"
          accept=".docx,.doc"
          onChange={handleFileChange}
          className="w-full p-2 border border-gray-300 rounded mt-2"
        />
      </div>
      
      {error && <div className="text-red-600 text-sm mb-4">{error}</div>}

      <button
        onClick={handleConvert}
        disabled={loading}
        className={`w-full py-2 px-4 bg-blue-600 text-white font-semibold rounded ${loading && "opacity-50 cursor-not-allowed"}`}
      >
        {loading ? "Converting..." : "Convert to PDF"}
      </button>

      {success && (
        <div className="mt-4 text-green-600 text-sm">
          Conversion successful! The file has been downloaded.
        </div>
      )}
    </div>
  );
};

export default DocToPdfConverter;
