import React, { useState, useRef } from "react";
import { renderAsync } from "docx-preview";
import html2pdf from "html2pdf.js";

const DocToPdfConverter = () => {
  const [file, setFile] = useState(null);
  const previewRef = useRef();

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  const handleConvert = async () => {
    if (!file) {
      alert("Please upload a file first.");
      return;
    }

    const reader = new FileReader();
    reader.onload = async (event) => {
      try {
        const arrayBuffer = event.target.result;

        // Render the .docx content to HTML
        await renderAsync(arrayBuffer, previewRef.current);

        // Make sure the preview container is hidden after rendering
        previewRef.current.style.display = "none";

        // Convert the rendered HTML to a PDF
        const pdfOptions = {
          margin: 1,
          filename: "converted.pdf",
          html2canvas: { scale: 2 },
          jsPDF: { unit: "in", format: "letter", orientation: "portrait" },
        };
        html2pdf().set(pdfOptions).from(previewRef.current).save();

        // Optionally clear the preview container after conversion
        previewRef.current.innerHTML = ""; // Clear the preview container if needed
      } catch (error) {
        console.error("Error converting file:", error);
      }
    };

    reader.readAsArrayBuffer(file);
  };

  return (
    <div className="doc-to-pdf-container">
      <div className="file-input-container">
        <input
          type="file"
          accept=".docx"
          onChange={handleFileChange}
          className="file-input"
        />
      </div>

      <button
        onClick={handleConvert}
        className="convert-button"
      >
        Convert to PDF
      </button>

      {/* Hidden container for the docx content (not displayed on screen) */}
      <div ref={previewRef} style={{ display: "none" }}></div>
    </div>
  );
};

export default DocToPdfConverter;
