import React, { useState } from "react";
import axios from "axios";

const DocToPdfConverter = () => {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    if (e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  const handleConvert = async () => {
    if (!file) {
      alert("Please upload a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://localhost:3001/convert", formData, {
        responseType: "blob", // Treat the response as a file
      });

      // Create a link to download the file
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `${file.name.split(".")[0]}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error("Error during conversion:", error);
      alert("Conversion failed. Please try again.");
    }
  };

  return (
    <div className="converter-container">
      <h1 className="title">Doc to PDF Converter</h1>
      <input
        type="file"
        accept=".docx"
        onChange={handleFileChange}
        className="file-input"
      />
      <button onClick={handleConvert} className="convert-button">
        Convert to PDF
      </button>
    </div>
  );
};

export default DocToPdfConverter;
